(63, NULL, 'Vesper', '{\"recipe\": [{\"Method\": \"Pour all ingredients into a cocktail shaker filled with ice cubes. Shake and strain into a chilled cocktail glass.\", \"Garnish\": \"Lemon zest\", \"Ingredients\": {\"Gin\": \"45 ml\", \"Vodka\": \"15 ml\", \"Lillet Blanc\": \"7.5 ml\"}}]}', 'Vesper.jpg', 'Contempory Classics'),
(64, NULL, 'Zombie', '{\"recipe\": [{\"Method\": \"Add all ingredients into an electric blender with 170 grams of cracked ice. With pulse bottom blend for a few seconds. Serve in a tall tumbler glass. Note: Donns Mix: 2 parts of fresh yellow grapefruit and 1 part of cinnamon syrup\", \"Garnish\": \"Garnish with mint leaves.\", \"Ingredients\": {\"Pernod\": \"6 Drops\", \"Falernum\": \"15 ml\", \"Donns Mix\": \"15 ml\", \"Demerara Rum\": \"30 ml\", \"Grenadine syrup\": \"1 Teaspoon\", \"Fresh lime juice\": \"20 ml\", \"Angostura bitters\": \"1 Dash\", \"Jamaican dark rum\": \"45 ml\", \"Gold Puerto Rican rum\": \"45 ml\"}}]}', 'Zombie.jpg', 'Contempory Classics'),
(65, NULL, 'Barracuda', '{\"recipe\": [{\"Method\": \"Pour all ingredients into cocktail shaker except the Prosecco, shake well with ice, strain into chilled highball glass filled with ice and top up with Prosecco.\", \"Garnish\": \"Pineapple and Cherry, optional mint sprig for additional aroma.\", \"Ingredients\": {\"Galliano\": \"15 ml\", \"Gold Rum\": \"45 ml\", \"Prosecco\": \"Top up\", \"Fresh Lime Juice\": \"1 Dash\", \"Fresh Pineapple Juice\": \"60 ml\"}}]}', 'Barracuda.jpg', 'New Era Drinks'),
(66, NULL, 'Bee\'s Knees', '{\"recipe\": [{\"Method\": \"Stir honey with lemon and orange juices until it dissolves, add gin and shake with ice. Strain into a chilled cocktail glass.\", \"Garnish\": \"Optionally garnish with a lemon or orange zest.\", \"Ingredients\": {\"Dry Gin\": \"52.5 ml\", \"Honey Syrup\": \"2 Teaspoons\", \"Fresh Lemon Juice\": \"22.5 ml\", \"Fresh Orange Juice\": \"22.5 ml\"}}]}', 'Bees-Knees.jpg', 'New Era Drinks'),
(67, NULL, 'Bramble', '{\"recipe\": [{\"Method\": \"Pour all ingredients into a cocktail shaker except the Creme de Mure, shake well with ice, strain into chilled old fashioned glass filled with crushed ice, then pour the blackberry liqueur (Creme de Mure) over the top of the drink, in a circular motion.\", \"Garnish\": \"Garnish optionally with a lemon slice and blackberries.\", \"Ingredients\": {\"Gin\": \"50 ml\", \"Sugar Syrup\": \"12.5 ml\", \"Creme de Mure\": \"15 ml\", \"Fresh Lemon Juice\": \"25 ml\"}}]}', 'Bramble.jpg', 'New Era Drinks'),
(68, NULL, 'Canchanchara', '{\"recipe\": [{\"Method\": \"Mix honey with water and lime juice and spread the mixture on the bottom and sides of the glass. Add cracked ice, and then the rum. End by energetically stirring from bottom to top.\", \"Garnish\": \"Lime wedge.\", \"Ingredients\": {\"Water\": \"50 ml\", \"Raw Honey\": \"15 ml\", \"Fresh Lime Juice\": \"15 ml\", \"Cuban Aguardiente\": \"60 ml\"}}]}', 'Canchanchara.jpg', 'New Era Drinks'),
(69, NULL, 'Dark \'n\' Stormy', '{\"recipe\": [{\"Method\": \"In a highball glass filled with ice pour the ginger beer and top floating with the Rum.\", \"Garnish\": \"Garnish with a lime wedge or slice.\", \"Ingredients\": {\"Ginger Beer\": \"100 ml\", \"Goslings Rum\": \"60 ml\"}}]}', 'DarkNStormy.jpg', 'New Era Drinks'),
(70, NULL, 'Espresso Martini', '{\"recipe\": [{\"Method\": \"Pour all ingredients into cocktail shaker, shake well with ice, strain into chilled cocktail glass.\", \"Garnish\": \"\", \"Ingredients\": {\"Vodka\": \"50 ml\", \"Kahlua\": \"30 ml\", \"Sugar Syrup\": \"10 ml\", \"strong Espresso\": \"1\"}}]}', 'Espresso-Martini.jpg', 'New Era Drinks'),
(71, NULL, 'Fernandito', '{\"recipe\": [{\"Method\": \"Pour the Fernet Branca into a double old fashioned glass with ice, fill the glass up with Cola. Gently stir.\", \"Garnish\": \"\", \"Ingredients\": {\"Cola\": \"Top Off\", \"Fernet Branca\": \"50 ml\"}}]}', 'Fernandito.jpg', 'New Era Drinks'),
(72, NULL, 'French martini', '{\"recipe\": [{\"Method\": \"Pour all ingredients into cocktail shaker, shake well with ice, strain into chilled cocktail glass.\", \"Garnish\": \"Squeeze oil from lemon peel onto the drink.\", \"Ingredients\": {\"Vodka\": \"45 ml\", \"Raspberry Liqueur\": \"15 ml\", \"Fresh Pineapple Juice\": \"15 ml\"}}]}', 'French-Martini.jpg', 'New Era Drinks'),
