(81, NULL, 'Russian Spring Punch', '{\"recipe\": [{\"Method\": \"Pour all ingredients into a cocktail shaker except the sparkling wine, shake well with ice, strain into a chilled tall tumbler glass filled with ice, and top up with sparkling wine.\", \"Garnish\": \"Garnish with blackberries and optionally a lemon slice as well.\", \"Ingredients\": {\"Vodka\": \"25 ml\", \"Sugar syrup\": \"10 ml\", \"Sparkling wine\": \"Top Off\", \"Creme de Cassis\": \"15 ml\", \"Fresh Lemon Juice\": \"25 ml\"}}]}', 'Russian-Spring-Punch.jpg', 'New Era Drinks'),
(82, NULL, 'Southside', '{\"recipe\": [{\"Method\": \"Pour all ingredients into a cocktail shaker, shake well with ice, double-strain into chilled cocktail glass. Note: If egg white is used shake vigorously.\", \"Garnish\": \"Garnish with mint springs.\", \"Ingredients\": {\"Egg White\": \"Few Drops (Optional)\", \"Mint Leaves\": \"5\", \"Simple Syrup\": \"15 ml\", \"London Dry Gin\": \"60 ml\", \"Fresh Lemon Juice\": \"30 ml\"}}]}', 'Southside.jpg', 'New Era Drinks'),
